import styled from "styled-components";

function Comment({ post, setPost }) {
    const onRemoveBtn = (k) => {
        if (k.myComment === false) return alert("본인이 작성한 댓글만 삭제할 수 있습니다.");
        window.confirm("정말 삭제 하시겠습니까?");
        setPost((prev) => ({
            ...prev,
            Comments: [...prev.Comments.filter((prev) => prev.User.nickname !== k.User.nickname)],
        }));
    };

    return (
        <S.CommentItem>
            <>
                <p>
                    작성자: <span>{el.User.nickname}</span>
                </p>
                <p>
                    댓글 내용: <span>{el.content}</span>
                </p>
                <button>수정</button>
                <button onClick={() => onRemoveBtn(el)}>삭제</button>
                <hr />
            </>
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
