import { useState } from "react";
import styled from "styled-components";

function Comment({ onRemoveBtn, list, onChangeEdit, post }) {
    const [onEdit, setOnEdit] = useState(false);

    const onEditBtn = (id, content) => {
        setOnEdit((prev) => !prev);
        const newPost = { ...post };
        const comment = newPost.Comments.find((comment) => comment.id === id);
        comment.content = content;
        setPost(newPost);
    };

    return (
        <S.CommentItem>
            <>
                <p>
                    작성자: <span>{list.User.nickname}</span>
                </p>

                <p>
                    댓글 내용:
                    {onEdit ? (
                        <>
                            <input onChange={onChangeEdit} />

                            <button onClick={onEditBtn}>수정완료</button>
                        </>
                    ) : (
                        <span>{list.content}</span>
                    )}
                </p>
                {list.myComment === true ? (
                    <>
                        <button onClick={() => onEditBtn(list)}>수정</button>
                        <button onClick={() => onRemoveBtn(list)}>삭제</button>
                    </>
                ) : (
                    ""
                )}
            </>
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
