import { useRef, useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
    /* 
    문제 1.

    state를 다루기 위한 기초 문제입니다.
    음악 목록 10가지의 mock data가 있습니다.

    아래에 추가버튼을 눌러 목록에 리스트를 추가하고 
    삭제 버턴을 눌렀을 때 데이터가 삭제될 수 있도록 해주세요
  */

    const [title, setTitle] = useState();
    const [onState, setOnState] = useState(false);
    const [singer, setSinger] = useState();
    // const removeClick = useRef(null);
    const [onRemove, setOnRemove] = useState([PlayListMock.playlist]);

    console.log(PlayListMock.playlist);
    /* 데이터 콘솔에 찍어두었으니 확인해볼 것 */

    const addTitle = (e) => {
        setTitle(e.target.value);
    };

    const addSinger = (e) => {
        setSinger(e.target.value);
    };

    const onClickAdd = () => {
        PlayListMock.playlist.push({ title: title, signer: singer });
        setOnState((prev) => !prev);
        console.log(PlayListMock.playlist);
    };

    const onRemoveBtn = (el) => {
        PlayListMock.playlist.filter((el) => el !== el);
    };

    return (
        <>
            <h1>문제1</h1>
            <ul>
                {PlayListMock.playlist.map((el) => (
                    <li ref={removeClick}>
                        <h3>{el.title}</h3>
                        <p>{el.signer}</p>
                        <button onClick={onRemoveBtn}>삭제</button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input onChange={addTitle} />
                </p>
                <p>
                    가수/작곡 : <input onChange={addSinger} />
                </p>
                <p>
                    <button onClick={onClickAdd}>추가</button>
                </p>
            </div>
        </>
    );
}
export default State1;
