import { useRef, useState } from "react";
import PlayListMock from "../../__mock__/playList.json";

function State1() {
    /* 
    문제 1.

    state를 다루기 위한 기초 문제입니다.
    음악 목록 10가지의 mock data가 있습니다.

    아래에 추가버튼을 눌러 목록에 리스트를 추가하고 
    삭제 버턴을 눌렀을 때 데이터가 삭제될 수 있도록 해주세요
  */

    // const [onPlusSong, setOnPlusSong] = useState(false);
    const [addSong, setAddSong] = useState();
    const [addSinger, setAddSinger] = useState();
    const [onAddList, setOnAddList] = useState(false);
    const [musicList, setMusicList] = useState(PlayListMock.playlist);

    const addList = () => {
        PlayListMock.playlist.push({
            title: addSong.current.value,
            signer: addSinger.current.value,
        });
        setOnAddList((el) => !el);
        console.log(PlayListMock.playlist);
    };

    const removeBtn = (title) => {
        setMusicList(musicList.filter((el) => el.title !== title));
        console.log(musicList);
    };
    return (
        <>
            <ul>
                {PlayListMock.playlist.map((e) => (
                    <li>
                        <h3>{e.title}</h3>
                        <p>{e.signer}</p>
                        <button
                            onClick={() => {
                                removeBtn(e.title);
                            }}
                        >
                            삭제
                        </button>
                        <hr />
                    </li>
                ))}
            </ul>
            <div>
                <p>
                    곡명 : <input />
                </p>
                <p>
                    가수/작곡 : <input />
                </p>
                <p>
                    <button onClick={addList}>추가</button>
                </p>
            </div>
        </>
    );
}
export default State1;
