import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import productList from "../__mock__/products.json";
// useEffect 를 사용해서 마운팅 할때
// 상품번호를 비교하여 일치한다면 리뷰와 평점을 띄우도록 해야 함

function DetailPage() {
    // console.log(params.productNumber);
    const params = useParams();
    const { productNumber } = params;
    const onDetailPage = state.find((el) => el.productNumber === productNumber);

    const [addReview, setAddReview] = useState(false);
    const [state, setState] = useState(productList.products);
    const [reviews, setReviews] = useState({
        name: "",
        review: "",
        rating: "",
    });

    const { name, review, rating } = reviews;

    const onAddList = (e) => {
        const { name, value } = e.target;
        setReviews({
            ...review,
            [name]: value,
        });
        console.log(review);
    };

    const onAddReviewBtn = () => {
        setAddReview((prev) => !prev);
    };

    // const a = useEffect(() => {});
    return (
        <div>
            {/* 
      상세 페이지는 자유롭게 꾸미시면 됩니다.
      아직 해당 부분의 진도가 나가지 않았기 때문에 주소의 파람을 가지고 올 수 있는 방법은
      미리 콘솔에 찍어두었습니다.

      단, 없는 번호 상품으로 접근 시 state페이지로 돌아가도록 구현해주세요
    */}
            {/* {params.productNumber} */}
            <p>상품번호: {onDetailPage.productNumber} </p>
            <p>평점: {onDetailPage.productRating}</p>
            <p>리뷰 개수: {onDetailPage.productReview} </p>

            {!addReview ? (
                <div>
                    <input name="name" placeholder="이름" onChange={onAddList} />
                    <input name="review" placeholder="느낌평" onChange={onAddList} />
                    <input name="rating" placeholder="점수" onChange={onAddList} />
                    <button>작성 완료</button>
                </div>
            ) : (
                <div>
                    <button onClick={onAddReviewBtn}>리뷰 추가</button>
                </div>
            )}

            <hr />
            {onDetailPage.Review.map((el) => (
                <>
                    <p>이름 : {el.reviewer}</p>
                    <p>느낌평 : {el.review}</p>
                    <p>점수 : {el.rating}</p>
                    <hr />
                </>
            ))}
        </div>
    );
}
export default DetailPage;
