import { useState } from "react";
import styled from "styled-components";

function Comment({ lists }) {
    console.log(lists);

    const onRemoveBtn = (e) => {
        lists.filter((el) => el.User.nickname !== e);
    };

    return (
        <S.CommentItem>
            {lists.map((el) => (
                <>
                    <p>
                        작성자: <span>{el.User.nickname}</span>
                    </p>
                    <p>
                        댓글 내용: <span>{el.content}</span>
                    </p>
                    <button>수정</button>
                    <button
                        onClick={() => {
                            onRemoveBtn(el.User.nickname);
                        }}
                    >
                        삭제
                    </button>
                    <hr />
                </>
            ))}
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
