import styled from "styled-components";

function Comment() {
    return (
        <S.CommentItem>
            <>
                <p>
                    작성자: <span>fff</span>
                </p>
                <p>
                    댓글 내용: <span>ddd</span>
                </p>
                <button>수정</button>
                <button>삭제</button>
                <hr />
            </>
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

const S = {
    CommentItem,
};
