    return (
        <S.CommentItem>
            {post.map((el) => (
                <>
                    <p>
                        작성자: <span>{el.User.nickname}</span>
                    </p>
                    <p>
                        댓글 내용: <span>예시 내용</span>
                    </p>
                </>
            ))}
        </S.CommentItem>
    );
}
export default Comment;

const CommentItem = styled.li`
    border: 1px solid #000;
    margin: 10px;
`;

